--<ASN1.PDU CryptoConditions.Condition, CryptoConditions.Fulfillment>--
CryptoConditions DEFINITIONS AUTOMATIC TAGS ::= BEGIN
/** * CONTAINERS */
Condition ::= SEQUENCE { type ConditionType, featureBitmask OCTET STRING, fingerprint OCTET STRING, maxFulfillmentLength INTEGER (0..MAX) }
Fulfillment ::= SEQUENCE { type ConditionType, payload OCTET STRING }
ConditionType ::= INTEGER { preimageSha256(0), rsaSha256(1), prefixSha256(2), thresholdSha256(3), ed25519(4) } (0..65535)
/** * FULFILLMENT PAYLOADS */
-- For preimage conditions, the payload equals the preimage
PrefixSha256FulfillmentPayload ::= SEQUENCE { prefix OCTET STRING, subfulfillment Fulfillment }
ThresholdSha256FulfillmentPayload ::= SEQUENCE { threshold INTEGER (0..4294967295), subfulfillments SEQUENCE OF ThresholdSubfulfillment }
ThresholdSubfulfillment ::= SEQUENCE { weight INTEGER (0..4294967295) DEFAULT 1, condition Condition OPTIONAL, fulfillment Fulfillment OPTIONAL }
RsaSha256FulfillmentPayload ::= SEQUENCE { modulus OCTET STRING (SIZE(128..512)), signature OCTET STRING (SIZE(128..512)) }
Ed25519FulfillmentPayload ::= SEQUENCE { publicKey OCTET STRING (SIZE(32)), signature OCTET STRING (SIZE(64)) }
/** * FINGERPRINTS */
-- SHA-256 hash of the fingerprint contents Sha256Fingerprint ::= OCTET STRING (SIZE(32)) -- digest
-- 32-byte Ed25519 public key Ed25519Fingerprint ::= OCTET STRING (SIZE(32)) -- publicKey
/** * FINGERPRINT CONTENTS * * The content that will be hashed to arrive at the fingerprint. */
-- The preimage type hashes the raw contents of the preimage
PrefixSha256FingerprintContents ::= SEQUENCE { prefix OCTET STRING, condition Condition }
ThresholdSha256FingerprintContents ::= SEQUENCE { threshold INTEGER (0..4294967295), subconditions SEQUENCE OF ThresholdSubcondition }
ThresholdSubcondition ::= SEQUENCE { weight INTEGER (0..4294967295), condition Condition }
RsaSha256FingerprintContents ::= INTEGER (0..MAX) -- modulus
